import styled from 'styled-components'
import style from './../../App.css'
import {NavLink as Link} from 'react-router-dom'
import {FaBars} from 'react-icons/fa'

export const Nav = styled.nav`
background-color: #862325;
display: flex;
justify-content: center;

`

export const NavLink = styled(Link)`
color: #F9ECE4;
display: flex;
align-items: center;
text-decoration: none;
padding: 0 1rem;
height: 100%;
cursor: pointer;

&.active{
    color: #c4abac;
}
`


export const Bars = styled(FaBars)`
display: none;
color: #FCD1D1;

@media screen and (max-width: 768px){
    display: block;
    position: center;

    transform: translate(-100%, 100%);
    font-size: 1.8rem;
    cursor:pointer;

}
`


export const NavMenuLeft = styled.div`
grid-column: 1;
    display: flex;
    align-items: center;
    margin-left: 24px;


`

export const NavMenuCenter = styled.div`
grid-column: 2;
    display: flex;
    align-items: center;
    text-align: center;
    justify-content:center;

    @media screen and (max-width:768px){
        display: none;
    }
`



export const NavMenuRight = styled.div`
grid-column: 3;
    display: flex;
    align-items: center;
    padding: 10px;
    justify-content: flex-end;
    font-size: clamp(10px, 1rem + 2vw, 14px);

    @media screen and (max-width:768px){
        display: none;
    }
`

export const NavbarContainer = styled.div`
display: flex;
align-items: center;
text-align: center;
justify-content:center;
width: 100%;
`
